import React, { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import Sidebar from "../../components/common/Sidebar"
import { authService } from "../../api/auth"
import { clientSegmentsService } from "../../api/client-segments"
import "./DashboardStyles.css"

const ClientSegmentsPage = () => {
  const [clients, setClients] = useState([])
  const [filteredClients, setFilteredClients] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [currentUser, setCurrentUser] = useState(null)

  // Состояние для фильтров
  const [filters, setFilters] = useState({
    ageGroup: "ALL",
    gender: "ALL",
    profession: "",
  })

  // Загружаем опции для фильтров
  const ageGroups = [
    { value: "ALL", label: "Все возрасты" },
    ...clientSegmentsService.getAgeGroups(),
  ]
  const genders = [
    { value: "ALL", label: "Любой пол" },
    ...clientSegmentsService.getGenders(),
  ]

  const navigate = useNavigate()

  useEffect(() => {
    const user = authService.getCurrentUser()
    if (!user || (user.role !== "ADMIN" && user.role !== "MANAGER")) {
      navigate("/login")
      return
    }
    setCurrentUser(user)

    fetchClients()
  }, [navigate])

  // При изменении фильтров обновляем отфильтрованный список
  useEffect(() => {
    if (clients.length > 0) {
      setFilteredClients(clientSegmentsService.filterClients(clients, filters))
    }
  }, [clients, filters])

  const fetchClients = async () => {
    try {
      setLoading(true)
      const clientsData = await clientSegmentsService.getClientsWithAnswers()
      setClients(clientsData)
      setFilteredClients(clientsData) // Изначально показываем всех клиентов
      setError(null)
    } catch (err) {
      setError(
        "Ошибка при загрузке клиентов: " +
          (err.response?.data?.message || err.message)
      )
      console.error("Failed to fetch clients:", err)
    } finally {
      setLoading(false)
    }
  }

  const handleFilterChange = e => {
    const { name, value } = e.target
    setFilters({
      ...filters,
      [name]: value,
    })
  }

  const clearFilters = () => {
    setFilters({
      ageGroup: "ALL",
      gender: "ALL",
      profession: "",
    })
  }

  return (
    <div className="dashboard-container">
      <Sidebar user={currentUser} />
      <div className="dashboard-content">
        <div className="segments-header">
          <h1>Сегменты клиентов</h1>
        </div>

        {error && <div className="error-message">{error}</div>}

        <div className="filters-container">
          <h2>Фильтры</h2>
          <div className="filters-grid">
            <div className="filter-group">
              <label htmlFor="ageGroup">Возрастная группа:</label>
              <select
                id="ageGroup"
                name="ageGroup"
                value={filters.ageGroup}
                onChange={handleFilterChange}
              >
                {ageGroups.map(option => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="filter-group">
              <label htmlFor="gender">Пол:</label>
              <select
                id="gender"
                name="gender"
                value={filters.gender}
                onChange={handleFilterChange}
              >
                {genders.map(option => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="filter-group">
              <label htmlFor="profession">Профессия:</label>
              <input
                type="text"
                id="profession"
                name="profession"
                value={filters.profession}
                onChange={handleFilterChange}
                placeholder="Введите профессию"
              />
            </div>

            <button className="clear-filters-btn" onClick={clearFilters}>
              Сбросить фильтры
            </button>
          </div>
        </div>

        {loading ? (
          <div className="loading-spinner">Загрузка клиентов...</div>
        ) : (
          <div className="clients-list">
            <div className="results-summary">
              Найдено клиентов: <strong>{filteredClients.length}</strong> из{" "}
              {clients.length}
            </div>

            <table>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Имя</th>
                  <th>Email</th>
                  <th>Телефон</th>
                  <th>Возраст</th>
                  <th>Пол</th>
                  <th>Профессия</th>
                  <th>Регион</th>
                  <th>Дата регистрации</th>
                </tr>
              </thead>
              <tbody>
                {filteredClients.length === 0 ? (
                  <tr>
                    <td colSpan="9" className="no-data">
                      Клиенты не найдены
                    </td>
                  </tr>
                ) : (
                  filteredClients.map(client => (
                    <tr key={client.id}>
                      <td>{client.id}</td>
                      <td>{client.name}</td>
                      <td>{client.email}</td>
                      <td>{client.phone || "—"}</td>
                      <td>{getAgeGroupLabel(client.ageGroup)}</td>
                      <td>{getGenderLabel(client.gender)}</td>
                      <td>{client.profession || "—"}</td>
                      <td>{client.region || "—"}</td>
                      <td>{formatDate(client.clientSince)}</td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        )}

        {/* Подробная информация о клиенте (можно добавить отдельное модальное окно) */}
      </div>
    </div>
  )
}

// Вспомогательные функции форматирования данных
const getAgeGroupLabel = ageGroup => {
  const groups = {
    AGE_18_24: "18-24 года",
    AGE_25_34: "25-34 года",
    AGE_35_44: "35-44 года",
    AGE_45_54: "45-54 года",
    AGE_55_PLUS: "55+ лет",
  }
  return groups[ageGroup] || ageGroup || "—"
}

const getGenderLabel = gender => {
  const genders = {
    MALE: "Мужской",
    FEMALE: "Женский",
    OTHER: "Другой",
  }
  return genders[gender] || gender || "—"
}

const formatDate = dateString => {
  if (!dateString) return "—"

  try {
    const date = new Date(dateString)
    return date.toLocaleDateString("ru-RU")
  } catch (e) {
    return dateString
  }
}

export default ClientSegmentsPage
