import React, { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import { clientsService } from "../../api/clients"
import Sidebar from "../../components/common/Sidebar"
import { authService } from "../../api/auth"
import "./ClientsPage.css"

const ClientsPage = () => {
  const [clients, setClients] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [currentUser, setCurrentUser] = useState(null)

  // Состояние для фильтров
  const [filters, setFilters] = useState({
    ageGroup: "",
    gender: "",
    profession: "",
  })

  // Состояние для опций фильтров
  const [filterOptions, setFilterOptions] = useState({
    ageGroups: [],
    genders: [],
    professions: [],
  })

  // Состояние для отображения детальной информации клиента
  const [selectedClient, setSelectedClient] = useState(null)

  const navigate = useNavigate()

  useEffect(() => {
    const user = authService.getCurrentUser()
    if (!user || (user.role !== "ADMIN" && user.role !== "MANAGER")) {
      navigate("/login")
      return
    }
    setCurrentUser(user)

    fetchFilterOptions()
    fetchClients()
  }, [navigate])

  // Получение опций для фильтров
  const fetchFilterOptions = async () => {
    try {
      const options = await clientsService.getFilterOptions()
      setFilterOptions(options)
    } catch (err) {
      console.error("Failed to fetch filter options:", err)
    }
  }

  // Получение списка клиентов с ответами
  const fetchClients = async () => {
    try {
      setLoading(true)
      const data = await clientsService.getClientsWithAnswers()
      setClients(Array.isArray(data) ? data : [])
      setError(null)
    } catch (err) {
      setError(
        "Ошибка при загрузке клиентов: " +
          (err.response?.data?.message || err.message)
      )
      console.error("Failed to fetch clients:", err)
      setClients([])
    } finally {
      setLoading(false)
    }
  }

  // Применение фильтров
  const applyFilters = async () => {
    try {
      setLoading(true)
      // Формируем объект с непустыми фильтрами
      const activeFilters = Object.entries(filters).reduce(
        (acc, [key, value]) => {
          if (value) {
            acc[key] = value
          }
          return acc
        },
        {}
      )

      // Если фильтров нет, получаем всех клиентов
      if (Object.keys(activeFilters).length === 0) {
        await fetchClients()
        return
      }

      // Иначе получаем отфильтрованных клиентов
      const data = await clientsService.getFilteredClients(activeFilters)
      setClients(Array.isArray(data) ? data : [])
      setError(null)
    } catch (err) {
      setError(
        "Ошибка при применении фильтров: " +
          (err.response?.data?.message || err.message)
      )
      console.error("Failed to apply filters:", err)
    } finally {
      setLoading(false)
    }
  }

  // Сброс всех фильтров
  const resetFilters = () => {
    setFilters({
      ageGroup: "",
      gender: "",
      profession: "",
    })
    fetchClients()
  }

  // Обработчик изменения фильтров
  const handleFilterChange = e => {
    const { name, value } = e.target
    setFilters({
      ...filters,
      [name]: value,
    })
  }

  // Отображение детальной информации о клиенте
  const showClientDetails = client => {
    setSelectedClient(client)
  }

  // Закрытие модального окна с деталями клиента
  const closeClientDetails = () => {
    setSelectedClient(null)
  }

  // Получение читаемого значения для возрастной группы
  const getAgeGroupLabel = ageGroup => {
    const found = filterOptions.ageGroups.find(
      group => group.value === ageGroup
    )
    return found ? found.label : ageGroup
  }

  // Получение читаемого значения для пола
  const getGenderLabel = gender => {
    const found = filterOptions.genders.find(g => g.value === gender)
    return found ? found.label : gender
  }

  return (
    <div className="dashboard-container">
      <Sidebar user={currentUser} />
      <div className="dashboard-content">
        <div className="clients-header">
          <h1>Сегменты клиентов</h1>
        </div>

        {error && <div className="error-message">{error}</div>}

        {/* Фильтры */}
        <div className="filters-section">
          <h3>Фильтры</h3>
          <div className="filters-container">
            <div className="filter-group">
              <label htmlFor="ageGroup">Возрастная группа:</label>
              <select
                id="ageGroup"
                name="ageGroup"
                value={filters.ageGroup}
                onChange={handleFilterChange}
              >
                <option value="">Все возрасты</option>
                {filterOptions.ageGroups.map(group => (
                  <option key={group.value} value={group.value}>
                    {group.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="filter-group">
              <label htmlFor="gender">Пол:</label>
              <select
                id="gender"
                name="gender"
                value={filters.gender}
                onChange={handleFilterChange}
              >
                <option value="">Все</option>
                {filterOptions.genders.map(gender => (
                  <option key={gender.value} value={gender.value}>
                    {gender.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="filter-group">
              <label htmlFor="profession">Профессия:</label>
              <select
                id="profession"
                name="profession"
                value={filters.profession}
                onChange={handleFilterChange}
              >
                <option value="">Все профессии</option>
                {filterOptions.professions.map(profession => (
                  <option key={profession.value} value={profession.value}>
                    {profession.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="filter-actions">
              <button className="btn-apply-filter" onClick={applyFilters}>
                Применить
              </button>
              <button className="btn-reset-filter" onClick={resetFilters}>
                Сбросить
              </button>
            </div>
          </div>
        </div>

        {loading ? (
          <div className="loading-spinner">Загрузка...</div>
        ) : (
          <div className="clients-list">
            <table>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Имя</th>
                  <th>Email</th>
                  <th>Телефон</th>
                  <th>Возраст</th>
                  <th>Пол</th>
                  <th>Профессия</th>
                  <th>Регион</th>
                  <th>Действия</th>
                </tr>
              </thead>
              <tbody>
                {clients.length === 0 ? (
                  <tr>
                    <td colSpan="9" className="no-data">
                      Клиенты не найдены
                    </td>
                  </tr>
                ) : (
                  clients.map(client => (
                    <tr key={client.id}>
                      <td>{client.id}</td>
                      <td>{client.name}</td>
                      <td>{client.email}</td>
                      <td>{client.phone}</td>
                      <td>{getAgeGroupLabel(client.ageGroup)}</td>
                      <td>{getGenderLabel(client.gender)}</td>
                      <td>{client.profession}</td>
                      <td>{client.region}</td>
                      <td className="actions">
                        <button
                          className="btn-view"
                          onClick={() => showClientDetails(client)}
                        >
                          Подробнее
                        </button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        )}

        {/* Модальное окно с деталями клиента */}
        {selectedClient && (
          <div className="modal">
            <div className="modal-content">
              <span className="close" onClick={closeClientDetails}>
                &times;
              </span>
              <h2>Детали клиента</h2>

              <div className="client-details">
                <div className="detail-group">
                  <h3>Основная информация</h3>
                  <div className="detail-row">
                    <span className="detail-label">ID:</span>
                    <span className="detail-value">{selectedClient.id}</span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Имя:</span>
                    <span className="detail-value">{selectedClient.name}</span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Email:</span>
                    <span className="detail-value">{selectedClient.email}</span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Телефон:</span>
                    <span className="detail-value">{selectedClient.phone}</span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Возрастная группа:</span>
                    <span className="detail-value">
                      {getAgeGroupLabel(selectedClient.ageGroup)}
                    </span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Пол:</span>
                    <span className="detail-value">
                      {getGenderLabel(selectedClient.gender)}
                    </span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Профессия:</span>
                    <span className="detail-value">
                      {selectedClient.profession}
                    </span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Регион:</span>
                    <span className="detail-value">
                      {selectedClient.region}
                    </span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Клиент с:</span>
                    <span className="detail-value">
                      {new Date(
                        selectedClient.clientSince
                      ).toLocaleDateString()}
                    </span>
                  </div>
                </div>

                <div className="detail-group">
                  <h3>Дополнительная информация</h3>
                  <div className="detail-row">
                    <span className="detail-label">Предпочтение локации:</span>
                    <span className="detail-value">
                      {selectedClient.locationPreference === "ONLINE"
                        ? "Онлайн"
                        : selectedClient.locationPreference === "OFFLINE"
                        ? "Оффлайн"
                        : selectedClient.locationPreference}
                    </span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Частота использования:</span>
                    <span className="detail-value">
                      {selectedClient.usageFrequency === "DAILY"
                        ? "Ежедневно"
                        : selectedClient.usageFrequency === "WEEKLY"
                        ? "Еженедельно"
                        : selectedClient.usageFrequency === "MONTHLY"
                        ? "Ежемесячно"
                        : selectedClient.usageFrequency === "RARELY"
                        ? "Редко"
                        : selectedClient.usageFrequency}
                    </span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Источник:</span>
                    <span className="detail-value">
                      {selectedClient.source}
                    </span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Социальная сеть:</span>
                    <span className="detail-value">
                      {selectedClient.socialNetwork || "-"}
                    </span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Цель использования:</span>
                    <span className="detail-value">
                      {selectedClient.usagePurpose === "BUSINESS"
                        ? "Бизнес"
                        : selectedClient.usagePurpose === "PERSONAL"
                        ? "Личное"
                        : selectedClient.usagePurpose}
                    </span>
                  </div>
                </div>

                {/* Ответы клиента на вопросы */}
                <div className="detail-group">
                  <h3>Ответы на вопросы</h3>
                  {selectedClient.answers &&
                  selectedClient.answers.length > 0 ? (
                    <div className="answers-list">
                      {selectedClient.answers.map(answer => (
                        <div key={answer.id} className="answer-item">
                          <div className="answer-question">
                            {answer.questionText}
                          </div>
                          <div className="answer-text">{answer.answer}</div>
                          <div className="answer-date">
                            {new Date(answer.answeredAt).toLocaleString()}
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="no-answers">Нет ответов</div>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  )
}

export default ClientsPage
